import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios"
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import AddBooks from "./components/AddBooks.jsx";
import LoginPage from "./components/LoginPage.jsx"
import Register from "./components/Register.jsx";
import MainPage from "./components/MainPage.jsx";
import UpdateBooks from "./components/UpdateBooks.jsx";

function App() {
 
 
  const[books,setBooks]=useState([])
  const [isAuthenticated, setIsAuthenticated] = useState(false);
 const fetch=()=>{

  const token = localStorage.getItem('token');
  axios.get("http://localhost:4000/api/book/get",{
    headers: {
      Authorization: `Bearer ${token}`
    }
  }).then((response)=>{
    console.log(response.data)
     setBooks(response.data)
  }).catch((error)=>{
throw error
  })
}
 useEffect(()=>{
  const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
      fetch();
    }
 },[])
 const deleteBooks=(id)=>{
  const token = localStorage.getItem('token');
  axios.delete(`http://localhost:4000/api/book/delete/${id}`,{
    headers: {
      Authorization: `Bearer ${token}`
    }
  })
  .then(()=>{
    if (token) {
      setIsAuthenticated(true);
      fetch();
    }
  }).catch((error)=>{
      throw error
  })
 }
 const [query, setQuery] = useState('');
 const [filteredBooks, setFilteredBooks] = useState(books); // Default to showing all books
 const navigate = useNavigate();

 const handleSearch = () => {
   if (!query) {
     setFilteredBooks(books);
     return;
   }
   const lowerQuery = query.toLowerCase();
   const filtered = books.filter((book) => {
     return (
       book.title.toLowerCase().includes(lowerQuery) ||
       book.author.toLowerCase().includes(lowerQuery) ||
       book.description.toLowerCase().includes(lowerQuery) ||
       book.category.some((cat) => cat.toLowerCase().includes(lowerQuery))
     );
   });

   setFilteredBooks(filtered);
 };

 useEffect(() => {
   handleSearch();
 }, [query, books]); 




  
  return (

     <div>
      <nav className="navbar navbar-expand-lg bg-body-tertiary custom-navbar">
        <div className="container-fluid">
          <a className="navbar-brand custom-navbar-brand" href="#">BookStore</a>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <a className="nav-link active custom-nav-link" aria-current="page" href="#">Home</a>
              </li>
              <li className="nav-item">
                <a className="nav-link active custom-nav-link" onClick={() => navigate("/add")}>AddBooks</a>
              </li>
              <li className="nav-item">
                <a
                  className="nav-link active custom-nav-link"
                  onClick={() => navigate("/")}
                >
                  Logout
                </a>
              </li>
            </ul>
            <form className="d-flex custom-search-form" role="search" onSubmit={(e) => e.preventDefault()}>
              <div>
                <input
                  type="text"
                  value={query}
                  onChange={(e) => setQuery(e.target.value)}
                  placeholder="Search by title, author, or other fields"
                />
                <button type="button" onClick={handleSearch}>Search</button>
              </div>
            </form>
          </div>
        </div>
      </nav>

   <Router>
   <Routes>
          <Route path="/" element={<LoginPage  />} />
         <Route path="/main" element={<MainPage books={books} deleteBooks={deleteBooks} fetch={fetch} />} />
          <Route path="/register" element={<Register  />} />
          <Route path="/add" element={<AddBooks fetch={fetch}  />} />
          <Route
  path="/update"
  element={<UpdateBooks books={books} fetch={fetch} />}
/>
      </Routes>
      </Router>
  </div>
  )
}

export default App;
