import React, { useState } from 'react';

const BookSearch = ({books}) => {
  // Static list of books (this can be replaced by a dynamic data source later)
  const booksData = [
    {
      _id: '1',
      title: 'The Great Gatsby',
      author: 'F. Scott Fitzgerald',
      description: 'A novel about the American dream.',
      category: ['Fiction', 'Classic'],
      coverImage: 'https://example.com/gatsby.jpg',
      price: 10.99,
      createdAt: '1925',
    },
    {
      _id: '2',
      title: 'To Kill a Mockingbird',
      author: 'Harper Lee',
      description: 'A novel about racial injustice in the American South.',
      category: ['Fiction', 'Classic'],
      coverImage: 'https://example.com/mockingbird.jpg',
      price: 12.99,
      createdAt: '1960',
    },
    {
      _id: '3',
      title: '1984',
      author: 'George Orwell',
      description: 'A dystopian novel set in a totalitarian society.',
      category: ['Fiction', 'Dystopian'],
      coverImage: 'https://example.com/1984.jpg',
      price: 14.99,
      createdAt: '1949',
    },
    // Add more books as needed...
  ];

  const [query, setQuery] = useState('');
  const [filteredBooks, setFilteredBooks] = useState(booksData);

  const handleSearch = (event) => {
    const query = event.target.value;
    setQuery(query);

    // Filter books based on the query
    const filtered = books.filter((book) => {
      return (
        book.title.toLowerCase().includes(query.toLowerCase()) ||
        book.author.toLowerCase().includes(query.toLowerCase()) ||
        book.description.toLowerCase().includes(query.toLowerCase()) ||
        book.category.some((cat) =>
          cat.toLowerCase().includes(query.toLowerCase())
        )
      );
    });

    setFilteredBooks(filtered); // Update the filteredBooks state with the result
  };

  return (
    <div>
      <h1>Search Books</h1>

      {/* Search input */}
      <div>
        <input
          type="text"
          value={query}
          onChange={handleSearch}
          placeholder="Search by title, author, description, or category"
        />
      </div>

      {/* Display filtered results */}
      <div>
        {filteredBooks.length === 0 ? (
          <p>No books found.</p>
        ) : (
          filteredBooks.map((book) => (
            <div key={book._id} style={styles.bookContainer}>
              <img src={book.coverImage} alt={book.title} style={styles.coverImage} />
              <div>
                <h3>{book.title}</h3>
                <p><strong>Author:</strong> {book.author}</p>
                <p><strong>Description:</strong> {book.description}</p>
                <p><strong>Price:</strong> ${book.price}</p>
                <p><strong>Created At:</strong> {book.createdAt}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// Simple styles for the book container and image
const styles = {
  bookContainer: {
    display: 'flex',
    marginBottom: '20px',
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '8px',
  },
  coverImage: {
    width: '100px',
    height: '150px',
    marginRight: '20px',
  },
};

export default BookSearch;
