import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios"
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import AddBooks from "./components/AddBooks.jsx";
import LoginPage from "./components/LoginPage.jsx"
import Register from "./components/Register.jsx";
import MainPage from "./components/MainPage.jsx";
import UpdateBooks from "./components/UpdateBooks.jsx";
import BookDetailes from "./components/BookDetailes.jsx";

function App() {
 
 
  const[books,setBooks]=useState([])
  const [isAuthenticated, setIsAuthenticated] = useState(false);
 const fetch=()=>{

  const token = localStorage.getItem('token');
  axios.get("http://localhost:4000/api/book/get",{
    headers: {
      Authorization: `Bearer ${token}`
    }
  }).then((response)=>{
    console.log(response.data)
     setBooks(response.data)
  }).catch((error)=>{
throw error
  })
}
 useEffect(()=>{
  const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
      fetch();
    }
 },[])
 const deleteBooks=(id)=>{
  const token = localStorage.getItem('token');
  axios.delete(`http://localhost:4000/api/book/delete/${id}`,{
    headers: {
      Authorization: `Bearer ${token}`
    }
  })
  .then(()=>{
    if (token) {
      setIsAuthenticated(true);
      fetch();
    }
  }).catch((error)=>{
      throw error
  })
 }




  
  return  <Router>
   <Routes>
          <Route path="/" element={<LoginPage  />} />
         <Route path="/main" element={<MainPage books={books} deleteBooks={deleteBooks} fetch={fetch} />} />
          <Route path="/register" element={<Register  />} />
          <Route path="/add" element={<AddBooks fetch={fetch}  />} />
          <Route
  path="/update"
  element={<UpdateBooks books={books} fetch={fetch} />}
/>
<Route  path="/detailes" element={<BookDetailes/>}/>
      </Routes>
      </Router>
}

export default App;
