import React from 'react'

import {useNavigate } from 'react-router-dom';
function BookList({books,deleteBooks}) {
    const navigate = useNavigate();
   


  return (
    <div className="page-content">

      {books.map((el)=>{
      return  <div className="card">
      <div className="card-content">
        <img src={el.coverImage} alt={el.title} className="card-image" />
        <h2 className="card-title">{el.title}</h2>
        <p className="card-author">{el.author}</p>
        <div className="card-buttons">
          <button className="btn update-btn" >Update</button>
          <button className="btn delete-btn" onClick={() => deleteBooks(el._id)}>Delete</button>
        </div>
        <button className="btn view-btn" onClick={() => navigate("/details")}>View Details</button>
      </div>
    </div>

      })}
      
    </div>
  )
}

export default BookList
