import React,{useState,useEffect} from 'react'
import axios from 'axios';
import BookList from './BookList';
import AddBooks from './AddBooks';
import LoginPage from './LoginPage';
function MainPage() {
   
    const [view,setView]=useState("main")
   
  const changeview=(view)=>{
   setView(view)
  }
  const renderview=()=>{
    if(view==="main"){
        return <BookList/>
    }else if(view==="AddBook"){
        return <AddBooks/>
    }else if(view==="Logout"){
        return <LoginPage/>
    }
  }
  return (
    <div>
      <nav className="navbar navbar-expand-lg bg-body-tertiary">
  <div className="container-fluid">
    <a className="navbar-brand" href="#">Navbar</a>
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <a className={view === "main" ? "nav-selected" : "nav-unselected"} aria-current="page" href="#"   >Home</a>
        </li>
        <li className="nav-item">
          <a className={view === "AddBook" ? "nav-selected" : "nav-unselected"} aria-disabled="page" onClick={() =>  changeview("addBook")}  >AddBooks</a>
        </li>
        <li className="nav-item">
          <a className={view === "Logout" ? "nav-selected" : "nav-unselected"} aria-disabled="page" onClick={() =>  changeview("Lo")}>Logout</a>
        </li>
      </ul>
      <form className="d-flex" role="search">
        <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
        <button className="btn btn-outline-success" type="submit">Search</button>
      </form>
    </div>
  </div>
</nav>
{renderview()}
    </div>
  )
}

export default MainPage
