import React from 'react'

import {useNavigate } from 'react-router-dom';
function BookList({books,deleteBooks}) {
    const navigate = useNavigate();
   


  return (
    <div className="book-card-container">
  {books.map((el, index) => (
    <div className="book-card-square" key={index}>
      <img src={el.coverImage} className="book-card-img" alt={el.title} />
      <div className="book-card-content">
        <h5 className="book-card-title">{el.title}</h5>
        <p className="book-card-author">by {el.author}</p>
        <h6 className="book-card-category">{el.category}</h6>
        <button onClick={()=>deleteBooks(el._id)}>Delete</button>
        <button onClick={()=>useNavigate("/update")}  >Update</button>
      </div>
    </div>
  ))}
</div>

  )
}

export default BookList
