import React,{useState,useEffect} from 'react'
import axios from 'axios';
import BookList from './BookList';
import {useNavigate } from 'react-router-dom';
function MainPage({books,deleteBooks,fetch}) {
  const [query, setQuery] = useState('')
    const navigate = useNavigate();

    const handleSearch = async () => {
      if (!query) {
        console.log('Please enter a search query');
        return;
      }
  
      try {
        const token = localStorage.getItem('token'); 
       await axios.get(`/api/books/search?query=${query}`,{
        headers: {
          Authorization: `Bearer ${token}`
        }
});
      } catch (err) {
        console.error('Error fetching books:', err);

      }
    };
  
   
  
  
  return (
    <div>
     <nav className="navbar navbar-expand-lg bg-body-tertiary custom-navbar">
  <div className="container-fluid">
    <a className="navbar-brand custom-navbar-brand" href="#">Navbar</a>
    <button
      className="navbar-toggler"
      type="button"
      data-bs-toggle="collapse"
      data-bs-target="#navbarSupportedContent"
      aria-controls="navbarSupportedContent"
      aria-expanded="false"
      aria-label="Toggle navigation"
    >
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0">
        <li className="nav-item">
          <a className="nav-link active custom-nav-link" aria-current="page" href="#">Home</a>
        </li>
        <li className="nav-item">
          <a className="nav-link active custom-nav-link" aria-disabled="page"  onClick={() => navigate("/add")}>AddBooks</a>
        </li>
        <li className="nav-item">
          <a
            className="nav-link active custom-nav-link"
            aria-disabled="page"
            onClick={() => navigate("/")}
          >
            Logout
          </a>
        </li>
      </ul>
      <form className="d-flex custom-search-form" role="search">
      <div>
        <input 
          type="text" 
          value={query} 
          onChange={(e) => setQuery(e.target.value)} 
          placeholder="Search by title, author, or other fields"
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      
      </form>
      
    </div>
  </div>
</nav>
<BookList books={books} deleteBooks={deleteBooks} />
    </div>
  )
}

export default MainPage
