import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios"
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import AddBooks from "./components/AddBooks.jsx";
import LoginPage from "./components/LoginPage.jsx"
import Register from "./components/Register.jsx";
import MainPage from "./components/MainPage.jsx";
import UpdateBooks from "./components/UpdateBooks.jsx";
import BookDetailes from "./components/BookDetailes.jsx";
import Categories from "./components/Categories";

function App() {
 
 
  const[books,setBooks]=useState([])
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [bookcat,setBookcat]=useState([])

const fetchbook=async (catid="all")=>{
  try {
    const token = localStorage.getItem('token');
    const endpoint=catid==="all"
    ?"http://localhost:4000/api/shoes"
    :`http://localhost:4000/api/shoes/category/${catid}`
    const response= await axios.get(endpoint,{
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    console.log(response.data)
    setBooks(response.data)
  }catch (error) {
    throw error;
  }
}
const fetchbookcat= async ()=>{
  try {
    const token = localStorage.getItem('token');
    const bookcat=await axios.get("http://localhost:4000/api/shoes/category",{
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    setBookcat(bookcat.data)
  } catch (error) {
    console.log(error);
    
  }
}
 useEffect(()=>{
  const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
      fetch();
      fetchbook()
      fetchbookcat()
    }
 },[])
 const deleteBooks=(id)=>{
  const token = localStorage.getItem('token');
  axios.delete(`http://localhost:4000/api/book/delete/${id}`,{
    headers: {
      Authorization: `Bearer ${token}`
    }
  })
  .then(()=>{
    if (token) {
      setIsAuthenticated(true);
      fetch();
    }
  }).catch((error)=>{
      throw error
  })
 }



 const handleCatChange=(catid)=>{
  fetchbook(catid)
}
  
  return  <Router>
   <Routes>
          <Route path="/" element={<LoginPage  />} />
         <Route path="/main" element={<MainPage books={books} deleteBooks={deleteBooks} fetch={fetch} />} />
          <Route path="/register" element={<Register  />} />
          <Route path="/add" element={<AddBooks fetch={fetch}  />} />
          <Route
  path="/update"
  element={<UpdateBooks books={books} fetch={fetch} />}
/>
