import React, { useState } from 'react'
import axios from 'axios'
import { Input, Ripple, initMDB } from "mdb-ui-kit";

initMDB({ Input, Ripple });
function AddBooks() {
    const[author,setAuthor]=useState("")
    const[title,setTitle]=useState("")
    const[description,setDescription]=useState("")
    const[category,setCategory]=useState("")
    const[trending,setTrending]=useState("")
    const[coverImage,setCoverImage]=useState("")
    const[Price,setPrice]=useState("")
    const[createdAt,setCreatedAt]=useState("")

    const addbooks=(e,obj)=>{
        e.preventDefault()
        axios.post("http://localhost:4000/api/book/add",obj)
        .then(()=>{
            console.log("books added")
        }).catch((error)=>{
          throw error
        })
    }
  return (
    <div className='container'>
      <form>

  <div className="row mb-4">
    <div className="col">
      <div data-mdb-input-init className="form-outline">
        <input type="text" id="form6Example1" className="form-control" placeholder='author' defaultValue={author} onChange={()=>setAuthor(e)} />
        
      </div>
    </div>
    <div className="col">
      <div data-mdb-input-init className="form-outline">
        <input type="text" id="form6Example2" className="form-control" placeholder='title' defaultValue={title} />
      </div>
    </div>
  </div>


  <div data-mdb-input-init className="form-outline mb-4">
    <input type="text" id="form6Example3" className="form-control" placeholder='category' defaultValue={description} />
  </div>


  <div data-mdb-input-init className="form-outline mb-4">
    <input type="text" id="form6Example4" className="form-control" placeholder='trending' defaultValue={category} />
  </div>


  <div data-mdb-input-init className="form-outline mb-4">
    <input type="text" id="form6Example5" className="form-control" placeholder='coverImage' defaultValue={trending} />
  </div>


  <div data-mdb-input-init className="form-outline mb-4">
    <input type="number" id="form6Example6" className="form-control" placeholder='Price' defaultValue={coverImage} />
  </div>


  <div data-mdb-input-init className="form-outline mb-4">
  <input type="number" id="form6Example6" className="form-control" placeholder='createdAt' defaultValue={Price} />
  </div>
  <div data-mdb-input-init className="form-outline mb-4">
  <input type="number" id="form6Example6" className="form-control" placeholder='description' defaultValue={createdAt} />
  </div>
  <div className="form-check d-flex justify-content-center mb-4">
   
  </div>

  <button data-mdb-ripple-init type="button" className="btn btn-primary btn-block mb-4" >Place order</button>
</form>
    </div>
  )
}

export default AddBooks
