import { useState } from "react";
import "./App.css";
import axios from "axios"
import BookList from "./components/BookList.jsx";
import AddBooks from "./components/AddBooks.jsx";

function App() {
  const [view,setView]=useState("booklist")
  
  const renderview =(view)=>{
   if(view==="booklist"){
    return <BookList/>
   }else if(view==="AddBook"){
    return <AddBooks/>
   }

  }
  return <div>
    <div>
        <nav className=" nav">
          <div
            className={
              this.state.view !== "AddBook"
                ? "nav-unselected"
                : "nav-selected"
            }
            onClick={() => {
              this.changeView("AddBook");
            }}
          >
            Create Post
          </div>
          <div
            className={
              this.state.view === "bookList" ? "nav-selected" : "nav-unselected"
            }
            onClick={() => this.changeView("bookList")}
          >
            All Books
          </div>
          <div>
            {/* <Search handleLogout={this.handleLogout.bind(this)} /> */}
          </div>
        </nav>

        {renderview()}
      </div>
  </div>;
}

export default App;
