import React from 'react'

function BookList({books}) {

  return (
    <div>
    {books.map((el)=>{
       return<div className="container d-flex  .book-card {
        width: 18rem;
        border-radius: 10px;
        overflow: hidden;
        box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
        transition: transform 0.3s ease, box-shadow 0.3s ease;
        background-color: #fff;
      }
      
      .book-card:hover {
        transform: translateY(-5px);
        box-shadow: 0px 6px 12px rgba(0, 0, 0, 0.3);
      }
      
      .book-card-img {
        height: 250px;
        object-fit: cover;
        border-bottom: 2px solid #f0f0f0;
      }
      
      .book-card-body {
        padding: 15px;
        text-align: center;
      }
      
      .book-card-title {
        font-size: 1.2rem;
        font-weight: bold;
        color: #333;
        margin-bottom: 10px;
      }
      
      .book-card-author {
        font-size: 1rem;
        color: #777;
        margin-bottom: 10px;
      }
      
      .book-card-category {
        font-size: 1rem;
        font-weight: bold;
        color: #007bff;
      }
      
      .book-card .card-text {
        margin-bottom: 0.5rem;
        font-style: italic;
        color: #555;
      }
      ">
       <img src={el.coverImage} className="card-img-top book-card-img" alt={el.title} />
       <div className="card-body book-card-body">
         <h5 className="card-title book-card-title">{el.title}</h5>
         <p className="card-text book-card-author">{el.author}</p>
         <h5 className="card-title book-card-category">{el.category}</h5>
       </div>
     </div>
     
    })}
    </div>
  )
}

export default BookList
