import React,{useState} from 'react'
import { Route, Switch } from "react-router";
import MainPage from './MainPage';
import {useNavigate } from 'react-router-dom';
import axios from 'axios';
function LoginPage({onLoginSuccess}) {
  const navigate=useNavigate()
    const [activeTab, setActiveTab] = useState('login');
     const [loginData, setLoginData] = useState({ email: '', password: '' });
     const [isAuthenticated, setIsAuthenticated] = useState(false);
     const handleLogin = async (e,loginData) => {
      e.preventDefault();
      try {
        const response = await axios.post('http://localhost:4000/api/user/login', loginData);
        localStorage.setItem('token', response.data.token);
        setIsAuthenticated(true)
        navigate("/")
      } catch (error) {
        console.error('Login failed:', error);
      }
    };
    
  return (
    <div>
      <div className="container mt-5">
    

      <div className="tab-content">
          <form onSubmit={(e)=>handleLogin(e,loginData)}>
            <div className="form-outline mb-4">
              <input
                type="email"
                className="form-control"
                value={loginData.email}
                onChange={(e) => setLoginData({...loginData, email: e.target.value})}
                placeholder="Email"
              />
            </div>

            <div className="form-outline mb-4">
              <input
                type="password"
                className="form-control"
                value={loginData.password}
                onChange={(e) => setLoginData({...loginData, password: e.target.value})}
                placeholder="Password"
              />
            </div>

            <ul className="nav nav-pills nav-justified mb-3" role="tablist">
        <li className="nav-item" role="presentation">
          <button 
            onClick={() => setActiveTab('login')}
          >
            Login
           
          </button>
        </li>
        
      </ul>
          </form>
        </div>

    </div>
    </div>
  )
}

export default LoginPage
