import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios"
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import AddBooks from "./components/AddBooks.jsx";
import LoginPage from "./components/LoginPage.jsx"
import Register from "./components/Register.jsx";
import MainPage from "./components/MainPage.jsx";
import UpdateBooks from "./components/UpdateBooks.jsx";
import BookDetailes from "./components/BookDetailes.jsx";
import Categories from "./components/Categories";

function App() {
 
 
  const[books,setBooks]=useState([])
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const fetchshoes=async (catid="all")=>{
    try {
      const endpoint=catid==="all"
      ?"http://127.0.0.1:5000/api/book"
      :`http://127.0.0.1:5000/api/shoes/category/${catid}`
      const response= await axios.get(endpoint)
      console.log(response.data)
      setBooks(response.data)
    }catch (error) {
      throw error;
    }
  }
  const fetchshoescat= async ()=>{
    try {
      const shoescat=await axios.get("http://127.0.0.1:5000/api/shoes/category")
      setShoescat(shoescat.data)
    } catch (error) {
      console.log(error);
      
    }
  }
 useEffect(()=>{
  const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
      fetch();
    }
 },[])
 const deleteBooks=(id)=>{
  const token = localStorage.getItem('token');
  axios.delete(`http://localhost:4000/api/book/delete/${id}`,{
    headers: {
      Authorization: `Bearer ${token}`
    }
  })
  .then(()=>{
    if (token) {
      setIsAuthenticated(true);
      fetch();
    }
  }).catch((error)=>{
      throw error
  })
 }




  
  return  <Router>
   <Routes>
          <Route path="/" element={<LoginPage  />} />
         <Route path="/main" element={<MainPage books={books} deleteBooks={deleteBooks} fetch={fetch} />} />
          <Route path="/register" element={<Register  />} />
          <Route path="/add" element={<AddBooks fetch={fetch}  />} />
          <Route
  path="/update"
  element={<UpdateBooks books={books} fetch={fetch} />}
/>
<Route  path="/detailes" element={<BookDetailes/>}/>
<Route path="/categories" element={<Categories />} /> 
      </Routes>
      </Router>
}

export default App;
