import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';

function UpdateBooks({ fetch }) {
    const { state } = useLocation();
    const book = state?.book;

    const [author, setAuthor] = useState(book?.author || '');
    const [title, setTitle] = useState(book?.title || '');
    const [description, setDescription] = useState(book?.description || '');
    const [category, setCategory] = useState(book?.category || '');
    const [trending, setTrending] = useState(book?.trending || false);
    const [coverImage, setCoverImage] = useState(book?.coverImage  ||'');
    const [Price, setPrice] = useState(book?.Price || '');
    const [createdAt, setCreatedAt] = useState(book?.createdAt || '');

    const navigate = useNavigate();

    const updatebooks = () => {
        const token = localStorage.getItem('token');
        axios.put(`http://localhost:4000/api/book/update/${book._id}`, {
            author,
            title,
            description,
            category,
            trending,
            coverImage,
            Price,
            createdAt,
        }, {
            headers: {
                Authorization: `Bearer ${token}`,
            },
        })
        .then(() => {
            console.log("Book updated");
            fetch();
            navigate("/main");
        })
        .catch(error => {
            console.error(error);
        });
    };
return (
        <div className="container">
            <h2>Update Book</h2>
            <form>
                {/* Input fields for each property */}
                <input type="text" value={author} onChange={(e) => setAuthor(e.target.value)} placeholder="Author" />
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" />
                <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" />
                <input type="text" value={category} onChange={(e) => setCategory(e.target.value)} placeholder="Category" />
                <input type="text" value={coverImage} onChange={(e) => setCoverImage(e.target.value)} placeholder="Cover Image URL" />
                <input type="number" value={Price} onChange={(e) => setPrice(e.target.value)} placeholder="Price" />
                <input type="number" value={createdAt} onChange={(e) => setCreatedAt(e.target.value)} placeholder="Cre" />
                <button type="button" onClick={updatebooks}>Update Book</button>
            </form>
        </div>
    );
}

export default UpdateBooks;
