import React from 'react'

import {useNavigate } from 'react-router-dom';
function BookList({books,deleteBooks}) {
    const navigate = useNavigate();
   


  return (
    <ul className="card-list">
      {books.map((el) => (
  <li className="card" key={el.id}>
    <a
      className="card-image"
      href={el.coverImage}
      target="_blank"
      rel="noopener noreferrer"
      style={{
        backgroundImage: `url(${el.coverImage})`, 
      }}
      data-image-full={el.coverImage}
    >
      <img
        src={el.coverImage}
        alt={el.title || "Book Cover"} 
      />
    </a>
    <a
      className="card-description"
      href={el.link}
      target="_blank"
      rel="noopener noreferrer"
    >
      <h2>{el.title || "Untitled"}</h2> 
      <p>{el.author || "Unknown Author"}</p> {/* Dynamically set the author */}
    </a>
  </li>
))}

 
</ul>

  )
}

export default BookList
