import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios"
import {Route, Routes} from "react-router-dom";
import BookList from "./components/BookList.jsx";
import AddBooks from "./components/AddBooks.jsx";
import Login from "./components/login.jsx";
import Register from "./components/Register.jsx";

function App() {
  const[books,setBooks]=useState([])
  const [isAuthenticated, setIsAuthenticated] = useState(false);
 const fetch=()=>{

  const token = localStorage.getItem('token');
  axios.get("http://localhost:4000/api/book/get",{
    headers: {
      Authorization: `Bearer ${token}`
    }
  }).then((response)=>{
     setBooks(response.data)
  }).catch((error)=>{
throw error
  })
}
 useEffect(()=>{
  const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
      fetch();
    }
 },[])

 const handleLogin = async (e,loginData) => {
  e.preventDefault();
  try {
    const response = await axios.post('http://localhost:4000/api/user/login', loginData);
    localStorage.setItem('token', response.data.token);
    setIsAuthenticated(true)
  } catch (error) {
    console.error('Login failed:', error);
  }
};
const handleRegister = async (e,registerData) => {
  console.log("registerData",registerData);
  
  e.preventDefault();
  if (registerData.password !== registerData.repeatPassword) {
    alert("Passwords don't match!");
    return;
  }
  try {
    await axios.post('http://localhost:4000/api/user/register', registerData);
    
    // setActiveTab('login');
  } catch (error) {
    console.log(error.response)
    console.error('Registration failed:', error);
  }
};
const handleLoginSuccess = () => {
  setIsAuthenticated(true);
  fetch();
};






  
  return <div>
   <Routes>
    <Route/>
        <Route path="/book" element={<BookList />}>
          <Route path="/login" element={<Login/>} />
          <Route path="/register" element={<Register />} />
          <Route path="/add" element={<AddBooks />} />
        </Route>
      </Routes>
  </div>;
}

export default App;
