import React from 'react'
import { useNavigate, useLocation } from 'react-router-dom';
function BookDetailes() {
    const { state } = useLocation();
    const book = state?.book;
    console.log(book)
     const navigate = useNavigate();
  return (
    
    <div
  className="card"
  style={{
    width: "50rem", // Make the card wider
    margin: "2rem auto", // Center the card horizontally
    padding: "1.5rem", // Add padding for spacing
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)", // Add a subtle shadow for better aesthetics
    borderRadius: "8px", // Slightly rounded corners
    textAlign: "center", // Center the text content
  }}
>
  <img
    src={book.coverimage}
    className="card-img-top"
    alt="Book Cover"
    style={{
      width: "100%", 
      height: "auto", 
      borderRadius: "8px", 
      marginBottom: "1rem", 
    }}
  />
  <div className="card-body">
    <h2 className="card-title" style={{ fontSize: "2rem", marginBottom: "1rem" }}>
      {book.title}
    </h2>
    <h5 className="card-author" style={{ fontSize: "1.5rem", color: "#555" }}>
      Author: {book.author}
    </h5>
    <p
      className="card-text"
      style={{ fontSize: "1.2rem", margin: "1rem 0", color: "#666" }}
    >
      {book.description}
    </p>
    <h5
      className="card-trending"
      style={{ fontSize: "1.3rem", color: book.trending ? "green" : "red" }}
    >
      Trending: {book.trending ? "Yes" : "No"}
    </h5>
    <h5 className="card-price" style={{ fontSize: "1.5rem", marginTop: "1rem" }}>
      Price: ${book.Price}
    </h5>
    <h5 className="card-created" style={{ fontSize: "1rem", color: "#777" }}>
      Published On: {new Date(book.createdAt)}
    </h5>
  </div>
</div>

    
  )
}

export default BookDetailes
