function App() {


  
  return <div>
     <nav className=" nav">
          <div
            className={
              view !== "AddBook"
                ? "nav-unselected"
                : "nav-selected"
            }
            onClick={() => {
              changeView("AddBook");
            }}
          >
            Create Post
          </div>
          <div
            className={
             view === "BookList" ? "nav-selected" : "nav-unselected"
            }
            onClick={() => changeView("BookList")}
          >
            All Posts
          </div>
          <div>
          </div>
        </nav>

        {renderView()}
  </div>;
}

export default App;
