import React,{useState,useEffect} from 'react'
import axios from 'axios';

function MainPage() {
    const[books,setBooks]=useState([])
    const [isAuthenticated, setIsAuthenticated] = useState(false);
   const fetch=()=>{
  
    const token = localStorage.getItem('token');
    axios.get("http://localhost:4000/api/book/get",{
      headers: {
        Authorization: `Bearer ${token}`
      }
    }).then((response)=>{
       setBooks(response.data)
    }).catch((error)=>{
  throw error
    })
  }
   useEffect(()=>{
    const token = localStorage.getItem('token');
      if (token) {
        setIsAuthenticated(true);
        fetch();
      }
   },[])
  
   const handleLogin = async (e,loginData) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:4000/api/user/login', loginData);
      localStorage.setItem('token', response.data.token);
      setIsAuthenticated(true)
    } catch (error) {
      console.error('Login failed:', error);
    }
  };
  const handleRegister = async (e,registerData) => {
    console.log("registerData",registerData);
    
    e.preventDefault();
    if (registerData.password !== registerData.repeatPassword) {
      alert("Passwords don't match!");
      return;
    }
    try {
      await axios.post('http://localhost:4000/api/user/register', registerData);
      
      // setActiveTab('login');
    } catch (error) {
      console.log(error.response)
      console.error('Registration failed:', error);
    }
  };
  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    fetch();
  };
  return (
    <div>
      
    </div>
  )
}

export default MainPage
