import React,{useState,useEffect} from 'react'
import axios from 'axios';
import BookList from './BookList';

function MainPage() {
    const[books,setBooks]=useState([])
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [view,setView]=useState("main")
   const fetch=()=>{
  
    const token = localStorage.getItem('token');
    axios.get("http://localhost:4000/api/book/get",{
      headers: {
        Authorization: `Bearer ${token}`
      }
    }).then((response)=>{
       setBooks(response.data)
    }).catch((error)=>{
  throw error
    })
  }
   useEffect(()=>{
    const token = localStorage.getItem('token');
      if (token) {
        setIsAuthenticated(true);
        fetch();
      }
   },[])
  
   const handleLogin = async (e,loginData) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:4000/api/user/login', loginData);
      localStorage.setItem('token', response.data.token);
      setIsAuthenticated(true)
    } catch (error) {
      console.error('Login failed:', error);
    }
  };
  const handleRegister = async (e,registerData) => {
    console.log("registerData",registerData);
    
    e.preventDefault();
    if (registerData.password !== registerData.repeatPassword) {
      alert("Passwords don't match!");
      return;
    }
    try {
      await axios.post('http://localhost:4000/api/user/register', registerData);
      
    } catch (error) {
      console.log(error.response)
      console.error('Registration failed:', error);
    }
  };
  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    fetch();
  };
  const changeview=(view)=>{
   setView(view)
  }
  const renderview=()=>{
    if(view==="main"){
        return <BookList/>
    }
  }
  return (
    <div>
      <nav class="navbar navbar-expand-lg bg-body-tertiary">
  <div class="container-fluid">
    <a class="navbar-brand" href="#">Navbar</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="#">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link active" aria-disabled="page">AddBooks</a>
        </li>
        <li class="nav-item">
          <a class="nav-link active" aria-disabled="page">Logout</a>
        </li>
      </ul>
      <form class="d-flex" role="search">
        <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
        <button class="btn btn-outline-success" type="submit">Search</button>
      </form>
    </div>
  </div>
</nav>
    </div>
  )
}

export default MainPage
